import React from 'react';
import { Route } from 'react-router-dom';
import'./boutique.scss';
import VitrineOverview from '../../components/vitrine-overview/vitrine-overview';
import CollectionPage from '../collection/collection';
import { convertedtCollectionSnapshotToMap, firestore } from '../../firebase/firebase.utils';
import { initCollectionsData } from '../../redux/boutique/actions.boutuique';
import { connect } from 'react-redux';
import WithSpinner from '../../components/with-spiner/with-spinner.component';

const VitrineOverviewWithSpinner = WithSpinner(VitrineOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class Boutique extends React.Component {
    state = {
        loading:true
    }
    unsubscribFromSnapshot = null;
    componentDidMount(){
        const {initCollectionData} = this.props;
        const collectionRef = firestore.collection('collections');
        collectionRef.onSnapshot(async snapshot=>{
          const collectionMap =  convertedtCollectionSnapshotToMap(snapshot);

          initCollectionData(collectionMap);
          this.setState({loading:false})
        });
    }
    render(){
        const {match} = this.props
        const {loading} = this.props

        return(
            <div className='shop-page'>
                <Route exact path={`${match.path}`} render={props => (<VitrineOverviewWithSpinner isLoading={loading} {...props} />)} />
                <Route  path={`${match.path}/:collectionId`} render={props=>(<CollectionPageWithSpinner isLoading={loading} {...props} />)}  />
            </div>
        )
    }
}
    const mapDispactchToProps = dispatch => ({
        initCollectionData: collectionMap => dispatch(initCollectionsData(collectionMap))
    })
export default connect(null, mapDispactchToProps) (Boutique);
